mapper.select:
SELECT survey_image
FROM survey
WHERE survey_no = #{surveyNo}

SELECT COUNT(*)
FROM survey
WHERE survey_no = #{surveyNo}
  AND user_no = #{userNo}

SELECT *, survey_attend.user_no as attend_user_no, if(survey_attend.user_no = #{userNo}, false, true) as attend_check FROM (SELECT survey.survey_no, survey_title, survey_description, survey_image, survey_post_at, survey_closing_at, user.user_no, user.user_nickname, user.user_image, survey_status.survey_status_name, os.open_status_name, count(survey_attend.survey_no) AS attend_count, is_deleted
FROM survey LEFT JOIN survey_status ON survey.survey_status_no = survey_status.survey_status_no LEFT JOIN user ON user.user_no = survey.user_no LEFT JOIN open_status os ON survey.open_status_no = os.open_status_no LEFT JOIN survey_attend ON survey.survey_no = survey_attend.survey_no WHERE is_deleted = 0 AND survey.survey_status_no != 1
GROUP BY survey_status.survey_status_no, user.user_no, survey.survey_closing_at, os.open_status_no, survey.survey_no, survey_attend.survey_no ORDER BY survey_post_at DESC LIMIT 12 OFFSET #{page}) a LEFT JOIN survey_attend ON a.survey_no = survey_attend.survey_no LEFT JOIN survey_tag ON a.survey_no = survey_tag.survey_no LEFT JOIN tag ON survey_tag.tag_no = tag.tag_no GROUP BY survey_tag.survey_tag_no, a.survey_no, survey_attend.user_no, survey_attend.survey_attend_no;
